mapper.update:
UPDATE tb_user_award_map


        point=#{point},


        used_status=#{usedStatus}


WHERE
user_award_id=#{userAwardId}

mapper.select:
SELECT uwm.user_award_id, uwm.create_time,uwm.point,uwm.used_status, usered.user_id,usered.name, shop.shop_id,shop.shop_name, award.award_id,award.award_name, operator.user_id as operator_user_id,operator.name as operator_user_name FROM tb_user_award_map as uwm ,tb_person_info as usered ,tb_shop as shop ,tb_award as award ,tb_person_info as operator WHERE uwm.user_id=usered.user_id AND uwm.shop_id=shop.shop_id AND uwm.award_id=award.award_id AND uwm.operator_id=operator.user_id
AND usered.user_id=#{userAwardCondition.user.userId}
AND shop.shop_id=#{userAwardCondition.shop.shopId}
AND operator.user_id=#{userAwardCondition.operator.userId}
ORDER BY uwm.user_award_id DESC LIMIT #{rowIndex},#{pageSize}

SELECT uwm.user_award_id, uwm.create_time,uwm.point,uwm.used_status, usered.user_id,usered.name, shop.shop_id,shop.shop_name, award.award_id,award.award_name, operator.user_id as operator_user_id,operator.name as operator_user_name FROM tb_user_award_map as uwm ,tb_person_info as usered ,tb_shop as shop ,tb_award as award ,tb_person_info as operator WHERE uwm.user_id=usered.user_id AND uwm.shop_id=shop.shop_id AND uwm.award_id=award.award_id AND uwm.operator_id=operator.user_id AND
uwm.used_status=1
AND usered.user_id=#{userAwardCondition.user.userId}
AND shop.shop_id=#{userAwardCondition.shop.shopId}
AND operator.user_id=#{userAwardCondition.operator.userId}
ORDER BY uwm.user_award_id DESC LIMIT #{rowIndex},#{pageSize}
