mapper.update:
UPDATE tb_user_shop_map


        point=#{point}


WHERE
user_id=#{user.userId} and shop_id=#{shop.shopId}

mapper.select:
SELECT usm.user_shop_id,usm.create_time,usm.point, personinfo.user_id ,personinfo.name, shop.shop_id,shop.shop_name FROM tb_user_shop_map as usm , tb_shop as shop ,tb_person_info as personinfo WHERE usm.user_id=personinfo.user_id AND usm.shop_id=shop.shop_id
AND usm.point >= #{userShopMapCondition.point}
ORDER BY usm.user_shop_id DESC LIMIT #{rowIndex},#{pageSize}

SELECT COUNT(1) FROM tb_user_shop_map


        user_id= #{userShopMapCondition.user.userId}


        shop_id= #{userShopMapCondition.shop.shopId}


        point >=#{userShopMapCondition.point}
